UPDATE `locales_quest` SET `Objectives_loc8`='Убейте коварную жрицу Ведьмису и 20 стражей Аман\'заси из клана Порочной Ветви, после чего доложите старейшине племени Сломанного Клыка в Деревне Сломанного Клыка.$B$BКоварную жрицу Ведьмису и стражей Аман\'заси можно найти в верхней части Джинта\'Алора во Внутренних землях.' WHERE `entry`=7861;
